import * as THREE from 'three';
import { OrbitControls } from './OrbitControls.js'; 
import { geoJsonGeometry } from 'three-geojson-geometry';
import usData from './us-geojson.json';
import earthTexture from './textures/earth_texture4.jpg';

let scene, camera, renderer, controls, raycaster;

function init() {
    // Scene
    scene = new THREE.Scene();

    // Camera
    camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.z = 5;

    // Renderer
    renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    document.body.appendChild(renderer.domElement);

    // Handle window resizing
    window.addEventListener('resize', () => {
        renderer.setSize(window.innerWidth, window.innerHeight);
        camera.aspect = window.innerWidth / window.innerHeight;
        camera.updateProjectionMatrix();
    });

    // Ambient light
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.2);
    scene.add(ambientLight);

    // Directional light
    const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
    directionalLight.position.set(10, 10, 10);
    scene.add(directionalLight);

    // Point light
    const pointLight = new THREE.PointLight(0xffffff, 0.5);
    pointLight.position.set(-10, -10, -10);
    scene.add(pointLight);

    // Globe
    const geometry = new THREE.SphereGeometry(3, 50, 50);
    const texture = new THREE.TextureLoader().load(earthTexture);
    const material = new THREE.MeshStandardMaterial({ map: texture });
    const globe = new THREE.Mesh(geometry, material);
    scene.add(globe);

    // United States GeoJSON
    const usGeometry = geoJsonGeometry(usData, 3); // Convert GeoJSON to Three.js Geometry with radius 3
    const usMaterial = new THREE.MeshBasicMaterial({ color: 0xff0000, transparent: true, opacity: 0.5 });
    const usMesh = new THREE.Mesh(usGeometry, usMaterial);
    scene.add(usMesh);

    // Initialize OrbitControls
    controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.25;
    controls.rotateSpeed = 0.35;
    controls.enablePan = false;
    controls.mouseButtons = {
        LEFT: THREE.MOUSE.ROTATE,
        MIDDLE: THREE.MOUSE.DOLLY,
        RIGHT: THREE.MOUSE.ROTATE
    };

    // Initialize Raycaster
    raycaster = new THREE.Raycaster();

    // Add event listener for click events
    renderer.domElement.addEventListener('click', onClick, false);

    // Start animation loop
    animate();
}

// Handle click events
function onClick(event) {
    const mouse = new THREE.Vector2();
    mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
    mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;

    raycaster.setFromCamera(mouse, camera);

    const intersects = raycaster.intersectObjects(scene.children);

    if (intersects.length > 0) {
        const selectedObject = intersects[0].object;
        // Highlight the clicked region or perform any action
        selectedObject.material.color.set(0x00ff00); // Change color to green
        console.log('United States clicked!');
    }
}

// Animation loop
function animate() {
    requestAnimationFrame(animate);

    // Update controls
    controls.update();

    // Render scene
    renderer.render(scene, camera);
}

// Call init function to set up the scene
init();
